$(function () {
    $('[data-toggle="tooltip"]').tooltip()
});

$(document).ready(function() {
    $('#dataTable, #dataTableFlexible, #dataTableFixed, #table-brand, #table-department, #table-department-plan, #table-withdrawal-waiting, #table-registration-waiting, #table-registration-successful').DataTable({
        "language": {
            "url": '//cdn.datatables.net/plug-ins/1.13.7/i18n/vi.json'
        }
    });
});

$(document).ready(function() {
    function iformat(icon) {
        var originalOption = icon.element;
        return $('<span><i class="fa ' + $(originalOption).data('icon') + '"></i> ' + icon.text + '</span>');
    }
    $('.icons_select2').select2({
        width: "100%",
        templateSelection: iformat,
        templateResult: iformat,
        allowHtml: true
    });
});

